import InputField from './InputField'
import SearchButton from './SearchButton'
import ShowResults from './ShowResults'

class App extends React.Component {
  state={
    inputSphere:0,
    results:[]
  }
  componentDidMount=()=>{
    this.setState({results:result.data})
