    console.log("to be done")
  }
  onChange=(key,value)=>{
    this.setState({[key]:value})
  }
  render (){
    return(
      <div className="App">
        <InputField
          inputField={this.state.inputSphere}
          name="inputSphere"
          onChange={this.onChange}
        />
        <SearchButton
          onSearch={this.onSearch}
        />
        <ShowResults
          results={this.state.results}
        />
      </div>
    )
  }
}

export default App;

